mapper.select:
select count(*)
  from member
 where email=#{email}

mapper.update:
update member
   set user_pwd = #{userPwd}
 where email = #{email}

update member
   set paNo = pano.currval,
   	   user_kind = 2,
   	   origin_name = #{originName},
   	   change_name = #{changeName}
 where usNo = #{usNo}

update member
   set phone=#{phone}, 
   	   origin_name = #{originName},
   	   change_name = #{changeName}
 where pano=#{paNo}

update partner 
   set partner_intro=#{partnerIntro},
   	   account_name=#{accountName}, 
   	   account_num=#{accountNum},
	   pre_msg=#{preMsg}
 where pano=#{paNo}

update member
   set status = 'N'
 where usNo = #{mno}

update member
   set status = 'Y', YELLOW_CARD = 0
 where usNo = #{mno}

update partner
   set PARTNER_STATUS = 'N'
 where PANO = #{ptno}

update partner
   set PARTNER_STATUS = 'Y', PA_YELLOW_CARD = 0
 where PANO = #{ptno}
